        const dy = Math.abs(dp.y())
        const funcs = {
            left: (dx, dy) => dx,
            right: (dx, dy) => dx,
            up: (dx, dy) => dy,
            down: (dx, dy) => dy,
            x: (dx, dy) => dx,
