        let v = fvs.get(id);
        if (!v) {
            v = this.newFingerView();
            DocumentBody.shared().addSubview(v);
            fvs.atPut(id, v);
        }
        return v;
    }

    removeFingerViews () {
        const map = this.fingerViewMap();
        map.keysArray().forEach((id) => {
            const fingerView = map.get(id);
            fingerView.removeFromParentView();
        })
        map.clear();
        return this;
    }

