    getUrlParametersDict () {
        const parser = document.createElement('a');
        parser.href = window.location.href;
        debugger

        const query = parser.search.substring(1);
        const vars = query.split('&');
        const params = {};
        for (let i = 0; i < vars.length; i++) {
