                //assert(this.storeSelectionRange());
                updateElementHTML(this.element(), newValue);
                //this.element().innerHTML = newValue;
                //assert(this.restoreSelectionRange());
                console.log(">>>>>>>>>>>>>>>>>>>>>>>>>>>>> restored selection");

            //this.focus();
        } else {
            this.element().innerHTML = newValue;
        }
        */

        return this
    }

    forceRedisplay () {
