        const views = elements.map(e => this.firstViewForElement(e)).nullsRemoved()
        return views
    }

    firstViewForElement (e) {
        // search up the dom element parents to find one
        // associated with a DomView instance 

        while (e) {
            const view = e.domView()
            if (view) {
                return view
            }
            e = e.parentElement
        }

        return null
    }


}.initThisClass());

