    // subview path: StackView -> NavView -> ScrollView -> TilesView -> Tiles

    navView () {
        const sv = this.scrollView()
        if (!sv) {
            return null
        }
        return sv.parentView()
    }

    stackView () {
        const nv = this.navView()
        if (!nv) {
            return null
        }
        return nv.parentView()
    }

    /*
    browser () {
        return this.stackView()
    }
    */

