    // --- get orientation ---

    isVertical () {
        const sv = this.stackView()
        if (!sv) {
            return null
        }
        return sv.direction() === "right"
    }

    // --- stacking general ---

    stackTiles () {
        //this.assertTilesHaveParent()

        if (this.isVertical()) {
            this.stackTilesVertically()
        } else {
            this.stackTilesHorizontally()
        }
        return this
    }

