      EventManager.shared().safeWrapEvent(() => { 
        this.onXhrError(event) 
      }, event)
    });

    xhr.addEventListener("abort", (event) => {
      EventManager.shared().safeWrapEvent(() => { 
        this.onXhrAbort(event) 
      }, event)
    });

    this.sendDelegate("onRequestBegin");
    this.sendDelegate("onStreamStart");

    xhr.send(this.body());

    return this.xhrPromise();
  }

