      slot.setIsSubnodeField(true)
      slot.setCanEditInspection(true)
      //slot.setSummaryFormat("value")
    }

    {
      const slot = this.newSlot("content", null);
      slot.setInspectorPath("")
      //slot.setLabel("prompt")
