    return this.peerMessages().peerConn()
  }

  isConnected () {
    return this.peerConn().isConnected()
  }

  // --- sending ---

  send () {
    this.peerConn().send(this.content()) // content should be valid JSON
    this.setStatus("sent")
  }

  sendActionInfo () {
    return {
      isEnabled: this.isConnected()
    }
  }

  // -- receiving ---

  onReceived () {
    this.setStatus("received")
  }

}.initThisClass());

