      this.shutdown()
    }
  }

  // --- receiving ping & sending pong ---

  onPing () {
    //console.log(this.typeId() + " onPing")
    this.sendPong();
  }

  sendPong () {
    //console.log(this.typeId() + " sendPong")
    this.send({ name: "RzPeerConnPong" })
  }

}.initThisClass());

